        var bool=true
        fetch(`https://localhost:44317/api/Cseveges/kettofelhasznalo?kuldo=${user}&kapo=${nemen}`)
            .then(response => {
                return response.json()
            })
            .then(data => {
                data.forEach(adat => {
                    if (adat.Uzenet!= "") {
                        bool= false
                    }

                });/*
                if (data != null) {
                    bool=false
                }*/

            })
        return bool
    }

    fetch(`https://localhost:44317/api/Profil`)
        .then(response => {
            return response.json()
        })
        .then(data => {
            data.forEach(element => {
                if (user != element.Felhasznalonev) {
                    if (checkmessages(element.Felhasznalonev)) {
                        SendMessage(element.Felhasznalonev, "", true, true)
                    }
                    
                }
            });
        })
}
