import { React, useState } from "react"
import { useParams } from "react-router";
import konyv3 from "../assets/konyvek/konyv3.jpg"
import "./Termek.css"
//import { Termekek } from "../assets/scripts/termekek";

function Termek() {
    const productId = useParams(); // URL-ből kiolvassuk az ID-t
    var Termekek = [{
        id: 2,
        Image: konyv3,
        Name: "konyv3",
        Price: 300
    },{
        id: 3,
        Image: konyv3,
        Name: "konyv3",
        Price: 300
    }, {
        id: 4,
        Image: konyv3,
        Name: "konyv3",
        Price: 300
    }]
    const product = Termekek.find(p => p.id == productId.termekId); // Megkeressük a terméket
    return (
        <div className="maintermek">
            <div>
                <button>&lt;</button>
                <img src={product.Image} alt={product.Name} />
                <button>&gt;</button>
            </div>
            <div>
                <h1>{product.Name}</h1>
                <h2>{product.Price}</h2>
                <button>Üzenet</button>

            </div>
        </div>
    );
};

export default Termek;
