import { deletemessages } from "./deletemessages"

export function nologin() {
    try {
        document.querySelector("#chatselection").classList.add("chatselection")
    } catch (error) {
        
    }
    if (localStorage.getItem("user") == null) {
        window.location.href = "http://localhost:3000/Login"
    }
    var kezdo = localStorage.getItem("ujbeszelgetes")
    if (kezdo != null) {
        localStorage.removeItem("ujbeszelgetes")
    }
    if(window.location.href != "http://localhost:3000/Chat"){
        deletemessages()
        try {
            document.querySelector("#chatselection").classList.remove("chatselection")
        } catch (error) {
            
        }
    }
    if(window.location.href == "http://localhost:3000/NewBook"){
        deletemessages()
        try {
            document.querySelector("#chatselection").classList.add("chatselection")
        } catch (error) {
            
        }
    }
}
