import { React, useState, useEffect } from "react"
import './Chat.css'
import bookielogo from '../assets/bookieonlylogo.png'
import { Link } from 'react-router-dom';
import { SendMessage } from "../assets/scripts/sendmessage";

function Chat() {
    const [chat, setChat] = useState()
    const [uzenetek, setUzenetek] = useState()
    const [user, setUser] = useState()
    const [element, setElement] = useState()
    function MessagesGen(user) {
        setElement(user)
        localStorage.setItem("utsobeszelgetes",user)
        fetch(`https://localhost:44317/api/Cseveges/kettofelhasznalo?kuldo=${JSON.parse(localStorage.getItem("user")).Felhasznalonev}&kapo=${user}`)
            .then(response => {
                return response.json()
            })
            .then(data => {
                var uzenet = []
                data.forEach((adat) => {
                    uzenet.push(
                        <li className={adat.KapoFelhasznalonev == JSON.parse(localStorage.getItem("user")).Felhasznalonev ? "me" : "you"}>
                            <div className="entete">
                                <h3>{adat.Datum}</h3>
                            </div>
                            <div className="message">
                                {adat.Uzenet}
                            </div>
                        </li>)
                })
                setUser(<header id="userbg">
                    <h2>{user}</h2>
                </header>)
                setUzenetek(uzenet)
            })
    }
    useEffect(() => {
        fetch(`https://localhost:44317/api/Cseveges/${JSON.parse(localStorage.getItem("user")).Felhasznalonev}`)
            .then(response => {
                return response.json()
            })
            .then(data => {
                var chats = []
                data.forEach(element => {
                    chats.push(
                        <li id={`${element}id`} onClick={() => { MessagesGen(element) }}>
                            <div>
                                <h2>{element}</h2>
                            </div>
                        </li>)
                })
                setChat(chats)
            })
    })
    document.body.addEventListener("load",()=>{
        var lastuser=localStorage.getItem("utsobeszelgetes")
        if(lastuser){
            setTimeout(() => {
                document.getElementById(`${lastuser}id`).click()  
            }, 100);
        }
    },true)
    return (
        <div id="container">
            <aside>
                <header>
                    <input type="text" placeholder="KeresÃ©s" />
                </header>
                <ul id="chatek">
                    {chat}
                </ul>
            </aside>
            <div className="main">
                {user}
                <ul id="chat">
                    {uzenetek}
                </ul>
                <footer>
                    <textarea id="uzenet" placeholder="Ãrd be az Ã¼zeneted"></textarea>
                    <a id="uzenetkuld" onClick={()=>{SendMessage(element)}}>KÃ¼ldÃ©s</a>
                </footer>
            </div>
        </div>
    )
}

export default Chat
