import { React } from "react"
import './Eladas.css'
import { SearchResultsList } from "./SearchResultsList";
import { Link } from 'react-router-dom';
import { SellProduct } from "../assets/scripts/newproduct";
import { useState } from "react"

function Eladas () {
    function uploadproduct() {
        if (document.getElementById("konyvvv").value != "") {
            handleUpload()
            SellProduct()
        }
    }
    //searchbar
    const [input, setInput] = useState("");
    const [results, setResults] = useState([]);
    const fetchData = (value) => {
      fetch("https://jsonplaceholder.typicode.com/users")
        .then((response) => response.json())
        .then((json) => {
          const results = json.filter((user) => {
            return (
              value &&
              user &&
              user.name &&
              user.name.toLowerCase().includes(value)
            );
          });
          setResults(results);
        });
    };
  
    const handleChange = (value) => {
      setInput(value);
      fetchData(value);
    };
    //searchbar
    //kep
    const [file, setFile] = useState(null);
    const handleUpload = async () => {
        if (!file) return alert("Válassz egy fájlt!");

        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await fetch("http://localhost:5000/upload", {
                method: "POST",
                body: formData,
            });

            const data = await response.json();
            setImageUrl(data.imageUrl);
        } catch (error) {
            console.error("Hiba a feltöltés során:", error);
        }
    };

    //kep
    return (
        <div className="eladas">
            <div className="input-wrapper">
                <input className="search"
                    placeholder="Type to search..."
                    value={input}
                    onChange={(e) => handleChange(e.target.value)}
                />
                <SearchResultsList results={results} />
                
            </div>
            <div>
                <span>Ár: </span>
                <input type="number" id="prizeinput" />
            </div>
            <div>
                <span>Csere</span>
                <input type="checkbox" name="Csere" id="cserebox" />
            </div>
            <div>
                <span>Kép: </span>
                <input type="file" accept=".png, .jpeg, .jpg" id="inputfile" onChange={handleFileChange} />
            </div>
            <div></div>
            <div>
                <button id="elad" onClick={uploadproduct}>Eladás</button>
            </div>
        </div>
    )
}

export default Eladas;
