import { React } from "react"
import './Eladas.css'
import { SearchResultsList } from "./SearchResultsList";
import { Link } from 'react-router-dom';
import { SellProduct } from "../assets/scripts/newproduct";
import { useState } from "react"

function Eladas() {
    //searchbar
    const [input, setInput] = useState("");
    const [kep, setKep] = useState("");
    const [results, setResults] = useState([]);
    const fetchData = (value) => {
        fetch("https://jsonplaceholder.typicode.com/users")//https://localhost:44317/api/Konyv
            .then((response) => response.json())
            .then((json) => {
                const results = json.filter((user) => {
                    return (
                        value &&
                        user &&
                        user.name &&
                        user.name.toLowerCase().includes(value)
                    );
                });
                setResults(results);
            });
    };

    const handleChange = (value) => {
        setInput(value);
        fetchData(value);
    };
    //searchbar
    function uploadproduct() {
        const reader = new FileReader();

        //let value = URL.createObjectURL(document.getElementById("inputfile").files[0]);
        if (document.getElementById("konyvvv").value != "") {
            //console.dir(document.getElementById("inputfile").files)
            //SellProduct(document.getElementById("inputfile").files[0].path)

            reader.readAsDataURL(document.getElementById("inputfile").files[0]); // Vagy readAsText(), readAsArrayBuffer()
            reader.onload = () => {
                SellProduct(reader.result)
            };
        }
    }
    return (
        <div className="eladas">
            <div className="input-wrapper">
                <input className="search"
                    id="konyvvv"
                    placeholder="Type to search..."
                    value={input}
                    onChange={(e) => handleChange(e.target.value)}
                />
                <SearchResultsList results={results} />

            </div>
            <div>
                <span>Ár: </span>
                <input type="number" id="prizeinput" />
            </div>
            <div>
                <span>Csere</span>
                <input type="checkbox" name="Csere" id="cserebox" />
            </div>
            <div>
                <span>Kép: </span>
                <input type="file" accept=".png, .jpeg, .jpg" id="inputfile" />
            </div>
            <div></div>
            <div>
                <button id="elad" onClick={uploadproduct}>Eladás</button>
            </div>
        </div>
    )
}

export default Eladas;
