import { useNavigate } from "react-router-dom";

export function getProducts() {
    const navigate = useNavigate();
    function handleClick(id) {
        navigate(`/Termek/${id}`);
    };
    fetch("https://localhost:44317/api/EladoTermek")
        .then((response) => {
            return response.json()
        })
        .then(data => {
            var main=document.getElementById("getProducts()")
            var card
            data.forEach((adat) => {
                if (adat.Aktiv) {
                    card=(<div class="termek" onClick={() => { handleClick(adat.Id) }}>
                        <div><img src={adat.Kep[0].EleresiUt} /></div>
                        <div><span id='termekname'>{adat.Konyv.Cim}</span></div>
                        <div><span id='termekprice'>{adat.Ar} Ft</span><span id='termekdistance' >{adat.Ar} km</span></div>
                    </div>)}})
            main.appendChild(card)
        })
        .catch((error) => {
            console.error("Hiba történt:", error);
        })
}
