import { useParams } from "react-router-dom";
import konyv3 from "../assets/konyvek/konyv3.jpg"
//import { Termekek } from "../assets/scripts/termekek";

function Termek(){
  const { productId } = useParams(); // URL-ből kiolvassuk az ID-t
  var Termekek={
    id: 3,
    Image: konyv3,
    Name: "konyv3",
    Price: 300,
    Distance: 30
}
  const product = Termekek.find(p => p.id == productId); // Megkeressük a terméket

  if (!product) {
    return <h2>Termék nem található!</h2>;
  }

  return (
    <div>
             <div >
                <span >Explore our</span>
                <span> Programs </span>
                <span >to shape you</span>
            </div>
    </div>
  );
};

export default Termek;
