export function MarkSold(id, href) {
    if (true) {
        fetch(`https://localhost:44317/api/EladoTermek/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                Aktiv: false,
                EladasDatum: new Date().toISOString().substring(0, 10)
            })
        })
        .then(response=>{
            return response.json()
        })
        .then(data=>{
            localStorage.setItem(`termek${id}`,JSON.stringify(data))
            console.log(data)
        })
        window.location.href = href == "Profil" ? `http://localhost:3000/Profil/Termekeim/${id}` : `http://localhost:3000/Vasarlas/Termekeim/${id}`
    }
}
