import { React } from "react"
import './Eladas.css'
import { Link } from 'react-router-dom';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { SellProduct } from "../assets/scripts/newproduct";
import { useState } from "react"

function Eladas() {
    //kepfeltoltes
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [imageUrl, setImageUrl] = useState("");

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file) return alert("V√°lassz egy f√°jlt!");

        const formData = new FormData();
        formData.append("file", file);

        setUploading(true);

        try {
            const response = await fetch("http://localhost:5000/upload", {
                method: "POST",
                body: formData,
            });

            const data = await response.json();
            setImageUrl(data.link);
            alert("Felt√∂ltve! üî•");
        } catch (error) {
            console.error("Hiba a felt√∂lt√©s sor√°n:", error);
            alert("Hiba t√∂rt√©nt!");
        } finally {
            setUploading(false);
        }
    }

    function uploadproduct() {
        if (document.getElementById("konyvvv").value != "") {
            SellProduct();
            handleUpload();
        }
    }

    return (
        <div className="eladas">
            <div>
                <span>K√∂nyv: </span>
                <input type="text" id="konyvvv" />
                <select name="" id="">
                    <option value="">konyv1</option>
                    <option value="">konyv2</option>
                    <option value="">konyv3</option>
                    <option value="">konyv4</option>
                    <option value="">konyv5</option>
                </select>
                <Link to="/NewBook" id="nokonyv">Nincs itt a k√∂nyved?</Link>
            </div>
            <div>
                <span>√År: </span>
                <input type="number" id="prizeinput" />
            </div>
            <div>
                <span>Csere</span>
                <input type="checkbox" name="Csere" id="cserebox" />
            </div>
            <div>
                <span>K√©p: </span>
                <input type="file" accept=".png, .jpeg, .jpg" id="inputfile" onChange={handleFileChange} />
            </div>
            <div></div>
            <div>
                <button id="elad" onClick={uploadproduct} disabled={uploading}>Elad√°s</button>
            </div>
            <div>
                <img src={setImageUrl} alt="" />
            </div>
        </div>
    )
}

export default Eladas;
