export async function SellProduct() {
    var konyv = fetch(`https://localhost:44317/api/Konyv/${document.getElementById("konyvvv").value}`)
        .then((response) => {
            return response.json()
            
        })
    
    setInterval(x=>{fetch("https://localhost:44317/api/EladoTermek", {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            Ar: document.getElementById("prizeinput").value,
            Csere: document.getElementById("cserebox").checked/* ==true ? 1 : 0*/,
            Aktiv: true,
            FelrakasDatum: Date.now,
            EladasDatum: null,
            Felhasznalonev: JSON.parse(localStorage.getItem("user")).Felhasznalonev,
            KepId: 0,
            Kep: {
                Id:0,
                EleresiUt: "asd",
                KepNev: document.getElementById("inputfile").name
            },
            KonyvId: konyv.Id
        },
        console.log(JSON.parse(konyv.Id)))
    })
        .then((response) => {
            if (!response.ok) {
                if (response.status === 401) {
                    alert()
                }
                if (response.status === 404) {
                    alert()
                }
                else {
                    throw new Error(`HTTP hiba! Státuszkód: ${response.status}`)
                }
            }
            else {
                //console.log(response.body)
                alert("Sikeres feltöltés!")
            }
            return response.json()

        })
        .then(data => {
            window.location.href = "http://localhost:3000/"
        })
        .catch((error) => {
            console.error("Hiba történt:", error);
            alert("Server hiba. Kérlek próbáld meg később!");
        })},1000)
}
