import { React, useState } from "react"
import './Chat.css'
import bookielogo from '../assets/bookieonlylogo.png'
import { Link } from 'react-router-dom';
import { ChatsGenerate } from "../assets/scripts/chatsgenerate";

function Chat() {
    var uzenet = []
    var User = []
    const [chat, setChat] = useState()
    function messagesgen() {
        const idk2 = []
        for (var i = 0; i < 52; i++) {
            if (i % 2 == 0) {
                idk2.push({
                    Who: "me",
                    Username: "Username",
                    Time: "Time",
                    Message: "Message",
                })
            }
            else {
                idk2.push({
                    Who: "you",
                    Username: "Username",
                    Time: "Time",
                    Message: "Message",
                })
            }
        }

        idk2.forEach((idkk) => {
            uzenet.push(
                <li className={idkk.Who}>
                    <div className="entete">

                        <h3>{idkk.Time}</h3>
                    </div>
                    <div className="message">
                        {idkk.Message}
                    </div>
                </li>)
        })
    }
    function asd() {
        fetch(`https://localhost:44317/api/Cseveges/${JSON.parse(localStorage.getItem("user")).Felhasznalonev}`)
            .then(response => {
                return response.json()
            })
            .then(data => {
                var chats = []
                data.forEach(element => {
                    chats.push(
                        <li onClick={()=>{
                            fetch(`https://localhost:44317/api/Cseveges/kettofelhasznalo?kuldo=${JSON.parse(localStorage.getItem("user")).Felhasznalonev}&kapo=${element}`)
                            .then()
                        }}>
                            <div>
                                <h2>{element}</h2>
                            </div>
                        </li>)
                })
                setChat(chats)
            })
    }
    function usergen() {
        const idk2 = []
        idk2.push({
            Kep: bookielogo,
            Username: "Username",
        })

        idk2.forEach((idkk) => {
            User.push(
                <header id="userbg">
                    <img src={idkk.Kep} alt="" />
                    <h2>{idkk.Username}</h2>
                </header>)
        })
    }
    messagesgen()
    usergen()
    asd()
    return (
        <div id="container">
            <aside>
                <header>
                    <input type="text" placeholder="Keresés" />
                </header>
                <ul>
                    {chat}
                </ul>
            </aside>
            <div className="main">
                {User}
                <ul id="chat">
                    {uzenet}
                </ul>
                <footer>
                    <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/ico_picture.png" alt="" />
                    <textarea placeholder="Írd be az üzeneted"></textarea>
                    <a href="#">Küldés</a>
                </footer>
            </div>
        </div>
    )
}

export default Chat
