import { SendMessage } from "./sendmessage"

//chathez kell
export function MindenkinekUzenet(user) {
    function checkmessages() {
        var felhasznalo = [user]
        fetch(`https://localhost:44317/api/Cseveges/${user}`)
            .then(response => {
                return response.json()
            })
            .then(data => {
                data.forEach(adat => {
                    felhasznalo.push(adat.Felhasznalonev)
                });
            })
        return felhasznalo
    }

    fetch(`https://localhost:44317/api/Profil`)
        .then(response => {
            return response.json()
        })
        .then(data => {
            data.forEach(element => {
                if (user != element.Felhasznalonev) {
                    var felhasznalok = checkmessages()
                    felhasznalok.forEach(felhasznalo => {
                        if (element.Felhasznalonev != felhasznalo) {
                            SendMessage(element.Felhasznalonev, "", true, true)
                        }
                    });
                }
            });
        })
}
