import { React, useState, useEffect } from "react"
import bookielogo from '../assets/bookielogo.png'
import './Menu.css'
import { Link } from 'react-router-dom';

function Menu() {
    const [isOpen, setIsOpen] = useState(false);
    const [user, SetUser] = useState(null)
    useEffect(() => {
        let storedUser = localStorage.getItem("user")
        if (storedUser) {
            SetUser(storedUser)
        }
    }, [])
    var loggedin = (
        <ul className={`header_List ${isOpen ? "open" : ""}`}>
            <li><Link to="/Vasarlas">Vásárlás</Link></li>
            <li><Link to="/Eladas">Eladás</Link></li>
            <li><Link to="/Chat">Chat</Link></li>
            <li><Link to="/Profil">Profilom</Link></li>
        </ul>
    )
    var loggedout = (
        <ul className={`header_List ${isOpen ? "open" : ""}`}>
            <li><Link to="/Login">Bejelentkezés</Link></li>

        </ul>
    )
    return (
        <div className="header">
            <Link to="/Vasarlas"><img src={bookielogo} alt="log" className="logo" /></Link>
            <button className="menu-toggle" onClick={() => setIsOpen(!isOpen)}>
                ☰
            </button>
            {
                    !user ?
                        (
                            loggedout
                        ) : (loggedin)
                }
        </div>
    );
}
export default Menu;
