import './Vasarlas.css'
import { Link, useNavigate } from "react-router-dom";
import { React, useState, useEffect } from "react"


function Vasarlas() {
    const [product, setProduct] = useState("")
    const [konyvcim, setKonyvcim] = useState("")
    const [sort, setSort] = useState("asc")
    const [szerzoNev, setSzerzoNev] = useState("")
    const [trade, setTrade] = useState("")
    const navigate = useNavigate();
    function HandleClick(id) {
        navigate(`/Termek/${id}`);
    }
    const [felhasznalo, SetUser] = useState(null)
    useEffect(() => {
        let storedUser = localStorage.getItem("user")
        if (storedUser) {
            SetUser(storedUser)
        }
    }, [])

    function ProductClick(adat){
        var csere = document.getElementById("cseresearch")
        var asd=JSON.stringify({Trade: csere.checked, KonyvCim: konyvcim, Sort: sort, SzerzoNev: szerzoNev})
        localStorage.setItem("productsort",asd)
        //console.log(asd)
        HandleClick(adat)
    }
    document.body.addEventListener("load", () => {
        var productsort = JSON.parse(localStorage.getItem("productsort"))
        if (window.location.href == "http://localhost:3000/Vasarlas" || window.location.href == "http://localhost:3000/") {
            if (productsort) {
                console.log(productsort)
                setKonyvcim(productsort.KonyvCim)
                setSort(productsort.Sort)
                setSzerzoNev(productsort.SzerzoNev)
                setTrade(productsort.Trade)
            }
        }
    }, true)
    useEffect(() => {
        
        fetch("https://localhost:44317/api/EladoTermek")
            .then((response) => {
                return response.json()
            })
            .then(data => {
                var user = localStorage.getItem("user")
                var productsorts = localStorage.getItem("productsort")
                localStorage.clear()
                if (user != null) {
                    localStorage.setItem("user", user)
                    localStorage.setItem("productsort", productsorts)
                }
                var cards = []
                switch (sort) {
                    case ("asc"): data.sort(function (a, b) { return a.Ar - b.Ar })
                        break;
                    case ("desc"): data.sort(function (a, b) { return b.Ar - a.Ar })
                        break;
                    case ("closest"): data.sort(function (a, b) { return a.Ar - b.Ar })//idk
                        break;
                    case ("newest"): data.sort(function (a, b) { return new Date(a.FelrakasDatum).toISOString().substring(0, 10) > new Date(b.FelrakasDatum).toISOString().substring(0, 10) ? 1 : -1 })
                        break;
                    case ("oldest"): data.sort(function (a, b) { return new Date(a.FelrakasDatum).toISOString().substring(0, 10) < new Date(b.FelrakasDatum).toISOString().substring(0, 10) ? 1 : -1 })
                        break;
                }
                //console.log(data)
                data.forEach(adat => {
                    localStorage.setItem(`termek${adat.Id}`, JSON.stringify(adat))
                    if (adat.Aktiv) {
                        var card = (
                            <div className="termek">
                                <div><img src={adat.Kep[0].EleresiUt} onClick={() => { !felhasznalo ? window.location.href = "http://localhost:3000/Login" : ProductClick(adat.Id) }} /></div>
                                <div><span id='termekname'>{adat.Konyv.Konyv.Cim}</span></div>
                                <div><span id='termekprice'>{adat.Ar} Ft</span><span id='termekdistance' >{adat.Ar} km</span></div>
                            </div>)
                        var cserebox = document.getElementById("csereidspan")
                        var csere = document.getElementById("cseresearch")
                        if (csere != null) {
                            if (csere.checked) {
                                if (adat.Konyv.Konyv.Cim.toLowerCase().includes(konyvcim.toLowerCase()) && adat.Konyv.Szerzo.Nev.toLowerCase().includes(szerzoNev.toLowerCase())) {
                                    if (adat.Csere) {
                                        cards.push(card)
                                    }
                                }
                                cserebox.innerText = "Csak Csere"
                                cserebox.style.color = "green"
                            }
                            else {
                                if (adat.Konyv.Konyv.Cim.toLowerCase().includes(konyvcim.toLowerCase()) && adat.Konyv.Szerzo.Nev.toLowerCase().includes(szerzoNev.toLowerCase())) {
                                    cards.push(card)
                                }
                                cserebox.innerText = "Minden Termék"
                                cserebox.style.color = "black"
                            }
                        }
                    }
                });
                
                setProduct(cards)
            })
            .catch((error) => {
                console.error("Hiba történt:", error);
            })
    })
    function radioChange() {
        var cserekiiras = document.getElementById("csereidspan")
        /*
                            <input type="radio" name="cseresearchidk" id="nocsere" onChange={()=>{radioChange("red")}} onClick={()=>{setKivalasztott(false)}}  />
                    <input type="radio" name="cseresearchidk" id="csere"  onChange={()=>{radioChange("green")}} onClick={()=>{setKivalasztott(false)}} />
                    <input type="radio" name="cseresearchidk" id="mindketto"  onChange={()=>{radioChange("black")}} checked={kivalasztott} onClick={()=>{setKivalasztott(true)}} />
        */
    }
    return (
        <div className="content">
            <div className="sort">
                <span>Könyv címe: </span>
                <input type="text" onChange={(e) => { setKonyvcim(e.target.value) }} />
                <div>
                    <span id="csereidspan" >Minden Termék</span>
                    <input type="checkbox" name="" id="cseresearch" checked={trade} onChange={(e)=>{setTrade(!trade)}} />
                </div>
                <select name="" id="" onChange={(e) => { setSort(e.target.value) }}>
                    <option value="asc">Legolcsóbb</option>
                    <option value="desc">Legdrágább</option>
                    <option value="closest">Legközelebb</option>
                    <option value="newest">Legújabb</option>
                    <option value="oldest">Legrégebbi</option>
                </select>
                <span>Szerző neve: </span>
                <input type="text" onChange={(e) => { setSzerzoNev(e.target.value) }} />
            </div>
            <div className="termekek" id="termekek">
                {product}
            </div>
        </div>
    )
}

export default Vasarlas
