import { React, useState } from "react"
import { useParams } from "react-router";
import "./Termek.css"
import { SendMessage } from "../assets/scripts/sendmessage";
import { nologin } from "../assets/scripts/nologin";
import MyMap from "./Map";

function Termek() {
    nologin()
    const productId = useParams(); // URL-ből kiolvassuk az ID-t
    const product =JSON.parse(localStorage.getItem(`termek${productId.termekId}`)); // Megkeressük a terméket
    function uzenet(){
        SendMessage(product.Felhasznalonev,"")
        localStorage.setItem("utsobeszelgetes", product.Felhasznalonev)
        window.location.href="http://localhost:3000/Chat"
    }
    return (
        <div className="maintermek">
            <div>
                <img src={product.Kep[0].EleresiUt} alt={product.Konyv.Nev} />
            </div>
            <div>
                <span>{product.Konyv.Szerzo.Nev}:</span>
                <h1 id="termekcim">{product.Konyv.Konyv.Cim}</h1>
                <h2>{product.Ar} Ft</h2><h3> {product.Csere ? "Cserélhető" : ""}</h3>
                <span>
                    {product.Profil.Felhasznalonev}
                    {product.Profil.Felhasznalonev}
                </span>
                <button className="hatter" onClick={()=>{uzenet()}}>Üzenet</button>
                <span>
                    {product.Profil.Telepules.TelepulesNev}
                </span>
            <MyMap lati={product.Profil.Telepules.SzelessegiFok} longi={product.Profil.Telepules.HosszusagiFok} />
            </div>

        </div>
    );
};

export default Termek;
