import { React, useState } from "react"
import { useParams } from "react-router";
import konyv3 from "../assets/konyvek/konyv3.jpg"
//import { Termekek } from "../assets/scripts/termekek";

function Termek() {
    const  productId  = useParams(); // URL-ből kiolvassuk az ID-t
    var Termekek = [{
        id: 3,
        Image: konyv3,
        Name: "konyv3",
        Price: 300,
        Distance: 30
    },{
        id: 4,
        Image: konyv3,
        Name: "konyv3",
        Price: 300,
        Distance: 30
    }]
    var product
    setTimeout(() => {
        product = Termekek.find(p => p.id === productId.termekId); // Megkeressük a terméket
    }, 100);
    return (
        <div>
        <h1>{product.Name}</h1>
        {product.Image}
        <p>{product.Price} Ft</p>
        <p>{product.Distance} km</p>
      </div>
    );
};

export default Termek;
