import { React, useState } from "react"
import './Profil.css'
import '../Vasarlas/Vasarlas.css'
import { useNavigate } from "react-router-dom";
import { Patch } from "../assets/scripts/patch";
import { validation } from "../assets/scripts/validation";

function Profil() {
    var user = JSON.parse(localStorage.getItem("user"))
    function update(type) {
        var input = document.getElementById(type + "update")
        if (input.disabled) {
            input.disabled = false
        }
        else {
            input.disabled = true
        }
        switch (type) {
            case "fullname": {
                if (input.value == "") {
                    input.value = user.TeljesNev
                }
            }
            case "telefon": {
                if (input.value == "") {
                    input.value = user.Telefonszam
                }
            }
            case "telepules": {
                if (input.value == "") {
                    input.value = user.Telepules.TelepulesNev
                }
            }
        }
    }
    function save() {
        var joe=true
        document.querySelectorAll(".kiir").forEach(element=>{
            if(element.innerHTML!="") joe=false
        })
        if(joe){
            var oldpasswd = prompt("Kérlek add meg a jelszavad: ")
            Patch(oldpasswd)
        }

    }
    var bovebb;
    var updatepatch = (<div className="bovebben">
        <div>
            <span>Új Jelszó: </span>
            <input type="password" id="passwordupdate" onChange={(e)=>{validation("passwd",e.target.value,0)}} disabled={true} />
            <button className="bovebbenButton" onClick={() => { update("password") }}>Módosítás</button>
        </div>
        <div className="kiir hidden"></div>
        <div>
            <span>Teljes Név</span>
            <input type="text" id="fullnameupdate" placeholder={user.TeljesNev} disabled={true} />
            <button className="bovebbenButton" onClick={() => { update("fullname") }}>Módosítás</button>
        </div>
        <div>
            <span>Telefonszám: </span>
            <input type="text" id="telefonupdate" placeholder={user.Telefonszam} onChange={(e)=>{validation("telefonszam",e.target.value,1)}} disabled={true} />
            <button className="bovebbenButton" onClick={() => { update("telefon") }}>Módosítás</button>
        </div>
        <div className="kiir hidden"></div>
        <div>
            <span>Település: </span>
            <input type="text" id="telepulesupdate" placeholder={user.Telepules.TelepulesNev} onChange={(e)=>{validation("passwd",e.target.value,2)}} disabled={true} />
            <button className="bovebbenButton" onClick={() => { update("telepules") }}>Módosítás</button>
        </div>
        <div className="kiir hidden"></div>
        <button className="hatter" onClick={() => save()} >Mentés</button>
    </div>)
    const [bovebben, setbovebben] = useState(updatepatch)
    function profilesettings() {
        setbovebben(updatepatch)
    }
    const navigate = useNavigate();
    function myproducts() {
        function HandleClick(id) {
            navigate(`/Profil/Termekeim/${id}`);
        }
        var bovebbek = []
        var termekeim=[]
        for (let i = 0; i <= 1000000; i++) {
            
            var termek = JSON.parse(localStorage.getItem(`termek${i}`))
            if (termek != null) {
                if (termek.Felhasznalonev == user.Felhasznalonev) {
                    termekeim.push(termek)
                }
            }
        }
        termekeim.forEach(termek => {
            bovebb = (
                <div className="termek">
                    <div><img src={termek.Kep[0].EleresiUt} onClick={()=>HandleClick(termek.Id)} /></div>
                    <div><span id='termekname'>{termek.Konyv.Konyv.Cim}</span></div>
                    <div><span id='termekpriceprofil'>{termek.Ar} Ft</span></div>
                </div>)
            bovebbek.push(bovebb)
        });
        var legbovebb=(
            <div className="profiltermekek">
                {bovebbek}
            </div>
        )
        setbovebben(legbovebb)
    }
    function getintouch() {
        bovebb = (
            <div className="bovebben">
                <textarea id="getintouchTextarea">

                </textarea>
                <button id="getintouchButton">
                    Küldés
                </button>
            </div>
        )
        setbovebben(bovebb)
    }
    function logout() {
        localStorage.clear()
        window.location.href = "http://localhost:3000/Login"
    }


    return (

        <div className="settingspage">
            <div className="settings">
                <input type="button" value="Profilbeállítások" onClick={profilesettings} />
                <input type="button" value="Termékeim" onClick={myproducts} />
                <input type="button" value="Kapcsolat" onClick={getintouch} />
                <span id="copyright"> Minden jog fenntartva © <br /> bookie.hu <br /> 1.0.1 </span>
                <input type="button" value="Kijelentkezés" onClick={logout} />
            </div>
            <div className="bovebb">
                {bovebben}
            </div>
        </div>
    )
}

export default Profil
