import HandleClick from "./HandleClick"

export function getProducts() {
    fetch("https://localhost:44317/api/EladoTermek")
        .then((response) => {
            return response.json()
        })
        .then(data => {
            var main=document.getElementById("termekek")
            data.forEach((adat) => {
                if (adat.Aktiv) {
                    var termek=document.createElement("div")
                    termek.classList.add("termek")
                    termek.addEventListener('click',HandleClick(adat.Id))
        
                    var div1=document.createElement("div")
                    var img=document.createElement("img")
                    img.src=adat.Kep[0].EleresiUt
                    div1.appendChild(img)
        
                    var div2=document.createElement("div")
                    var span1=document.createElement("span")
                    span1.attributes.add('termekname')
                    span1.innerText=adat.Konyv.Cim
                    div2.appendChild(span1)
        
                    var div3=document.createElement("div")
                    var span2=document.createElement("span")
                    span2.attributes.add('termekprice')
                    span2.innerText=adat.Ar
                    div3.appendChild(span2)
                    var span3=document.createElement("span")
                    span3.attributes.add('termekdistance')
                    span3.innerText=adat.Ar + ' km'
                    div3.appendChild(span3)
        
                    termek.appendChild(div1)
                    termek.appendChild(div2)
                    termek.appendChild(div3)
                    main.appendChild(termek)
                    }})

        })
        .catch((error) => {
            console.error("Hiba történt:", error);
        })
}
