import './Vasarlas.css'
import { Link, useNavigate } from "react-router-dom";
import { React, useState, useEffect } from "react"


function Vasarlas() {
    const [product, setProduct] = useState("")
    const [konyvcim, setKonyvcim] = useState("")
    const navigate = useNavigate();
    function HandleClick(id) {
        navigate(`/Termek/${id}`);
    }
    const [felhasznalo, SetUser] = useState(null)
    useEffect(() => {
        let storedUser = localStorage.getItem("user")
        if (storedUser) {
            SetUser(storedUser)
        }
    }, [])
    useEffect(()=>{
        fetch("https://localhost:44317/api/EladoTermek")
        .then((response) => {
            return response.json()
        })
        .then(data => {
            var user=localStorage.getItem("user")
            localStorage.clear()
            if(user!=null){
                localStorage.setItem("user",user)
            }
            var cards = []
            data.forEach(adat => {
                localStorage.setItem(`termek${adat.Id}`,JSON.stringify(adat))
                var card = (
                    <div className="termek">
                        <div><img src={adat.Kep[0].EleresiUt} onClick={() => { !felhasznalo ? window.location.href="http://localhost:3000/Login" : HandleClick(adat.Id) }} /></div>
                        <div><span id='termekname'>{adat.Konyv.Konyv.Cim}</span></div>
                        <div><span id='termekprice'>{adat.Ar} Ft</span><span id='termekdistance' >{adat.Ar} km</span></div>
                    </div>)
                if(adat.Konyv.Konyv.Cim.toLowerCase().includes(konyvcim.toLowerCase()) && document.getElementById("cseresearch").checked==adat.Csere){
                    cards.push(card)
                }
            });
            setProduct(cards)
        })
        .catch((error) => {
            console.error("Hiba történt:", error);
        })
    })

    return (
        <div className="content">
            <div className="sort">
                <span>Könyv címe: </span>
                <input type="text" onChange={(e)=>{setKonyvcim(e.target.value)}} />
                <div>
                    <span>Csere</span>
                    <input type="checkbox" name="Csere" id="cseresearch" />
                </div>
                <select name="" id="">
                    <option value="">Legolcsóbb</option>
                    <option value="">Legdrágább</option>
                    <option value="">Legközelebb</option>
                    <option value="">Legújabb</option>
                    <option value="">Legrégebbi</option>
                </select>
                <span>Szerző neve: </span>
                <input type="text" />
            </div>
            <div className="termekek" id="termekek">
                {product}
            </div>
        </div>
    )
}

export default Vasarlas
