import { React, useState, useEffect } from "react"
import './Chat.css'
import bookielogo from '../assets/bookieonlylogo.png'
import { Link } from 'react-router-dom';
import { ChatsGenerate } from "../assets/scripts/chatsgenerate";

function Chat() {

    var User = []
    const [chat, setChat] = useState()
    const [uzenetek, setUzenetek] = useState()
    function MessagesGen(element){
        fetch(`https://localhost:44317/api/Cseveges/kettofelhasznalo?kuldo=${JSON.parse(localStorage.getItem("user")).Felhasznalonev}&kapo=${element}`)
        .then(response=>{
            return response.json()
        })
        .then(data=>{
            var uzenet = []
            data.forEach((adat) => {
                uzenet.push(
                    <li className={adat.KapoFelhasznalonev == JSON.parse(localStorage.getItem("user")).Felhasznalonev ? "me" : "you"}>
                        <div className="entete">                            
                            <h3>{adat.Datum}</h3>
                        </div>
                        <div className="message">
                            {adat.Uzenet}
                        </div>
                    </li>)
            })
            setUzenetek(uzenet)
        })
    }
    const [asd, asdA] = useEffect()
    asdA(()=>{
        fetch(`https://localhost:44317/api/Cseveges/${JSON.parse(localStorage.getItem("user")).Felhasznalonev}`)
            .then(response => {
                return response.json()
            })
            .then(data => {
                var chats = []
                data.forEach(element => {
                    chats.push(
                        <li onClick={() => {MessagesGen(element)}}>
                            <div>
                                <h2>{element}</h2>
                            </div>
                        </li>)
                })
                setChat(chats)
            })
    }) 
    function usergen() {
        const idk2 = []
        idk2.push({
            Kep: bookielogo,
            Username: "Username",
        })

        idk2.forEach((idkk) => {
            User.push(
                <header id="userbg">
                    <img src={idkk.Kep} alt="" />
                    <h2>{idkk.Username}</h2>
                </header>)
        })
    }
    usergen()
    asd()
    return (
        <div id="container">
            <aside>
                <header>
                    <input type="text" placeholder="Keresés" />
                </header>
                <ul>
                    {chat}
                </ul>
            </aside>
            <div className="main">
                {User}
                <ul id="chat">
                    {uzenetek}
                </ul>
                <footer>
                    <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1940306/ico_picture.png" alt="" />
                    <textarea placeholder="Írd be az üzeneted"></textarea>
                    <a href="#">Küldés</a>
                </footer>
            </div>
        </div>
    )
}

export default Chat
