import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";
import { useEffect, useRef } from "react";
import "./Termek.css"

function MyMap(lati){
    const mapRef = useRef(null);
    useEffect(() => {
        if (mapRef.current) {
            mapRef.current.panTo(defaultCenter); // Térkép középre állítása
        }
    }, []);
    const markerPosition = useMemo(() => ({
        lat: lati.lati,
        lng: lati.longi,
      }), []);
    const defaultCenter = {
        lat: 46.708704399274865, lng: 20.18879505023016,
      };
    return (
        <LoadScript googleMapsApiKey="AIzaSyBAz3NgvETAjFMzP88Q86sJRfLyJj0P0_g">
            <GoogleMap center={defaultCenter}
                zoom={7.3}
                onLoad={(map) => (mapRef.current = map)} // Mentjük a térkép referenciáját
                options={{
                    mapTypeControl: false,      // "Map" és "Satellite" gombok kikapcsolása
                    fullscreenControl: false,   // Teljes képernyő gomb kikapcsolása
                    streetViewControl: false,   // "Street View" ikon kikapcsolása
                    zoomControl: false,         // Zoom gombok kikapcsolása
                  }}
                mapContainerStyle={{ height: "500px", width: "900px"}}>
                <Marker position={markerPosition} />
            </GoogleMap>
        </LoadScript>
    );
};

export default MyMap;
