import { React, useEffect } from "react"
import './Eladas.css'
import { SearchResultsList } from "./SearchResultsList";
import { Link } from 'react-router-dom';
import { SellProduct } from "../assets/scripts/newproduct";
import { useState } from "react"

function Eladas() {
    //searchbar
    const [input, setInput] = useState("");
    const [results, setResults] = useState([]);
    const [termek, setTermek] = useState();
    const fetchData = (value) => {
        fetch("https://localhost:44317/api/Konyv")
            .then(response=> {
                return response.json()
            })
            .then((data) => {
                const results = data.filter((adat) => {
                    return (
                        value &&
                        adat &&
                        adat.Cim &&
                        adat.Cim.toLowerCase().includes(value.toLowerCase())
                    );
                });
                setResults(results);
            });
    };
    const handleChange = (value) => {
        setInput(value);
        fetchData(value);
    };
    //searchbar
    function uploadproduct() {
        if (document.getElementById("konyvvv").value != "") {
            const reader = new FileReader();
            reader.readAsDataURL(document.getElementById("inputfile").files[0]);
            reader.onload = () => {
                SellProduct(reader.result)
            };
        }
    }
    var eladotermek=JSON.parse(localStorage.getItem("eladotermek"))
    document.body.addEventListener("load",()=>{
        
        if(window.location.href=="http://localhost:3000/Eladas"){
            if(eladotermek!=null){
                setInput(eladotermek.Cim)
                
                setTermek(eladotermek)
                console.log(termek)
            }
        }
    },true)
    return (
        <div className="eladas">
            <div className="input-wrapper">
                <input className="search"
                    id="konyvvv"
                    placeholder="Type to search..."
                    value={input}
                    onChange={(e) => handleChange(e.target.value)}
                />
                <SearchResultsList results={results} />
                <button className="nokonyv" onClick={()=>{window.location.href="http://localhost:3000/NewBook"}}>
                    Nincs itt a könyved?
                </button>
            </div>
            <div>
                <span>Ár: </span>
                <input type="number" id="prizeinput" value={termek.Price} />
            </div>
            <div>
                <span>Csere</span>
                <input type="checkbox" name="Csere" id="cserebox" checked={termek.Trade} />
            </div>
            <div>
                <span>Kép: </span>
                <input type="file" accept=".png, .jpeg, .jpg" id="inputfile" files={termek.files} />
            </div>
            <div></div>
            <div>
                <button id="elad" onClick={uploadproduct}>Eladás</button>
            </div>
        </div>
    )
}

export default Eladas;
