import { useParams } from "react-router-dom";
//import { Termekek } from "../assets/scripts/termekek";

function Termek(){
  const { productId } = useParams(); // URL-ből kiolvassuk az ID-t
  const product = Termekek.find(p => p.id === productId); // Megkeressük a terméket

  if (!product) {
    return <h2>Termék nem található!</h2>;
  }

  return (
    <div>
      <h1>{product.name}</h1>
      <img src={product.image} alt={product.name} />
      <p>{product.description}</p>
      <p>{product.price} Ft</p>
    </div>
  );
};

export default Termek;
