import { React, useState } from "react"
import { useParams } from "react-router";
import konyv3 from "../assets/konyvek/konyv3.jpg"
//import { Termekek } from "../assets/scripts/termekek";

function Termek() {
    const productId = useParams(); // URL-ből kiolvassuk az ID-t
    var Termekek = [{
        id: 3,
        Image: konyv3,
        Name: "konyv3",
        Price: 300,
        Distance: 30,
    }, {
        id: 4,
        Image: konyv3,
        Name: "konyv3",
        Price: 300,
        Distance: 30,
    }]
    var card
    
    if(productId.termekId!=undefined){
        console.log(productId.termekId)
        var product
        Termekek.forEach((p) => {
            if (p.id === productId) {
                product = p
            }
        })
        card = (<div>
            <h1>{product.Name}</h1>
            {product.Image}
            <p>{product.Price} Ft</p>
            <p>{product.Distance} km</p>
        </div>)
    }
    return (
        <div>
            {card}
        </div>
    );
};

export default Termek;
