import fs from 'node:fs';
import path from 'node:path';
import express from 'express';
import multer from 'multer';

const { google } = require("googleapis");

const app = express();
const PORT = 5000;

// Service Account JSON betÃ¶ltÃ©se
const auth = new google.auth.GoogleAuth({
    keyFile: "./bookieimage.json", // A JSON fÃ¡jl neve
    scopes: ["https://www.googleapis.com/auth/drive.file"],
});

// Google Drive API inicializÃ¡lÃ¡sa
const drive = google.drive({ version: "v3", auth });

// Multer beÃ¡llÃ­tÃ¡sa (fÃ¡jlfeltÃ¶ltÃ©s)
const upload = multer({ dest: "uploads/" });

// ðŸ“Œ FÃ¡jl feltÃ¶ltÃ©se a Drive-ra
app.post("/upload", upload.single("file"), async (req, res) => {
    try {
        const fileMetadata = {
            name: req.file.originalname,
            parents: ["1rTc9ThTwwMPcwmwitlLAul4xT-g_3ebb"], // Itt a Drive mappa ID-jÃ¡t add meg!
        };

        const media = {
            mimeType: req.file.mimetype,
            body: fs.createReadStream(req.file.path),
        };

        const file = await drive.files.create({
            resource: fileMetadata,
            media: media,
            fields: "id",
        });

        // TÃ¶rÃ¶ljÃ¼k a feltÃ¶ltÃ¶tt fÃ¡jlt a szerverrÅ‘l
        fs.unlinkSync(req.file.path);

        res.status(200).json({ fileId: file.data.id, link: `https://drive.google.com/uc?id=${file.data.id}` });
    } catch (error) {
        console.error(error)
        res.status(500).json({ error: "FeltÃ¶ltÃ©si hiba" })
    }
});

// Szerver indÃ­tÃ¡sa
app.listen(PORT, () => console.log(`âœ… Szerver fut a ${PORT} porton`));
