import { React, useState } from "react"
import { useParams } from "react-router";
import "./Termek.css"
import { SendMessage } from "../assets/scripts/sendmessage";

function Termek() {
    const productId = useParams(); // URL-ből kiolvassuk az ID-t
    const product =JSON.parse(localStorage.getItem(`termek${productId.termekId}`)); // Megkeressük a terméket
    //console.log(product)
    function uzenet(){
        SendMessage(product.Felhasznalonev,"")
        localStorage.setItem("utsobeszelgetes", product.Felhasznalonev)
        window.location.href="http://localhost:3000/Chat"
    }
    return (
        <div className="maintermek">
            <div>
                <img src={product.Kep[0].EleresiUt} alt={product.Konyv.Nev} />
            </div>
            <div>
                <h2>{product.Konyv.Szerzo.Nev}:</h2>
                <h1>{product.Konyv.Konyv.Cim}</h1>
                <h2>{product.Ar} Ft</h2>
                <button className="hatter" onClick={()=>{uzenet()}}>Üzenet</button>

            </div>
        </div>
    );
};

export default Termek;
