import { React, useState } from "react"
import { useParams } from "react-router";
import "./Termek.css"
import { SendMessage } from "../assets/scripts/sendmessage";

function Termek() {
    const productId = useParams(); // URL-ből kiolvassuk az ID-t
    const product =JSON.parse(localStorage.getItem(`termek${productId.termekId}`)); // Megkeressük a terméket
    //console.log(product)
    function uzenet(){
        SendMessage(product.Felhasznalonev,"")
        localStorage.setItem("utsobeszelgetes", product.Felhasznalonev)
        window.location.href="http://localhost:3000/Chat"
    }
    return (
        <div className="maintermek">
            <div>
                <img src={product.Kep[0].EleresiUt} alt={product.Konyv.Nev} />
            </div>
            <div>
                <h3>{product.Konyv.Szerzo.Nev}:</h3>
                <h1 id="termekcim">{product.Konyv.Konyv.Cim}</h1>
                <h2>{product.Ar} Ft</h2>
                <button className="hatter" onClick={()=>{uzenet()}}>Üzenet</button>
                <iframe src={`https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d10823.546684267289!2d${"21.6371666666667"}!3d${"47.5288333333333"}!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x476ebb8ff8df702b%3A0xb4ba34a3f372a50b!2zR2VuY3NhcMOhdGksIEd5w7ZuZ3nDtnNhcMOhdGksIDk3MjE!5e0!3m2!1shu!2shu!4v1743537579855!5m2!1shu!2shu`} allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade" />
            </div>
        </div>
    );
};

export default Termek;
