import { React, useState } from "react"
import './Profil.css'
import { Patch } from "../assets/scripts/patch";

function Profil() {
    var user=JSON.parse(localStorage.getItem("user"))
    function update(type){
        var input=document.getElementById(type+"update")
        if(input.disabled){
            input.disabled=false
        }
        else{
            input.disabled=true
        }
        switch(type){
            case "fullname" : input.value=type.placeholder
        }
    }
    function save(){
        var oldpasswd=prompt("Kérlek add meg a jelszavad: ")
        if(oldpasswd==user.Jelszo){
            Patch(oldpasswd)
        }
        
    }
    var bovebb;
    var updatepatch = (<div className="bovebben">
        <div>
            <span>Új Jelszó: </span>
            <input type="password" id="passwordupdate" disabled={true} />
            <button onClick={()=>{update("password")}}>Módosítás</button>
        </div>
        <div>
            <span>Teljes Név</span>
            <input type="text" id="fullnameupdate" placeholder={user.TeljesNev} disabled={true} />
            <button onClick={()=>{update("fullname")}}>Módosítás</button>
        </div>
        <div>
            <span>Telefonszám: </span>
            <input type="text" id="telefonupdate" placeholder={user.Telefonszam} disabled={true} />
            <button onClick={()=>{update("telefon")}}>Módosítás</button>
        </div>
        <div>
            <span>Település: </span>
            <input type="text" id="telepulesupdate" placeholder={user.Telepules.TelepulesNev} disabled={true} />
            <button onClick={()=>{update("telepules")}}>Módosítás</button>
        </div>
        <button className="hatter"  onClick={()=>save()} >Mentés</button>
    </div>)
    const [bovebben, setbovebben] = useState(updatepatch)
    function profilesettings() {
        setbovebben(updatepatch)
    }
    function myproducts() {
        bovebb = (<div className="bovebben">
            <div>
                <span>Könyv: </span>
                <input type="text" />
                <select name="" id="">
                    <option value="">konyv1</option>
                    <option value="">konyv2</option>
                    <option value="">konyv3</option>
                    <option value="">konyv4</option>
                    <option value="">konyv5</option>
                </select>
                <button>Nincs itt a könyved?</button>
            </div>
        </div>)
        setbovebben(bovebb)
    }
    function getintouch(){    
        bovebb = (
            <div className="bovebben">
                <textarea id="getintouchTextarea">

                </textarea>
                <button id="getintouchButton">
                    Küldés
                </button>
            </div>
        )
        setbovebben(bovebb)
    }
    function logout(){
        localStorage.clear()
        window.location.href="http://localhost:3000/Login"
    }


    return (
        
        <div className="settingspage">
            <div className="settings">
                <input type="button" value="Profilbeállítások" onClick={profilesettings} />
                <input type="button" value="Termékeim" onClick={myproducts} />
                <input type="button" value="Kapcsolat" onClick={getintouch} />
                <span id="copyright"> Minden jog fenntartva © <br /> bookie.hu <br/> 1.0.1 </span>
                <input type="button" value="Kijelentkezés" onClick={logout} />
            </div>
            <div className="bovebb">
                {bovebben}
            </div>
        </div>
    )
}

export default Profil
