import { useState } from "react"
import { SendMessage } from "./sendmessage"

//chathez kell
export default function MindenkinekUzenet(user) {
    var felhasznalok=[]
    function checkmessages() {
        var felhasznalo = [user]
        fetch(`https://localhost:44317/api/Cseveges/${user}`)
            .then(response => {
                return response.json()
            })
            .then(data => {
                data.forEach(adat => {
                    felhasznalo.push(adat.Felhasznalonev)
                });
            })
            SetFelhasznalok(felhasznalo)
    }
    checkmessages()
    fetch(`https://localhost:44317/api/Profil`)
        .then(response => {
            return response.json()
        })
        .then(data => {
            data.forEach(element => {
                if (!felhasznalok.includes(element.Felhasznalonev)) {
                    SendMessage(element.Felhasznalonev, "", true, true)
                }
            });
        })
}
