import { SendMessage } from "./sendmessage"

//chathez kell
export function MindenkinekUzenet(user) {

    function checkmessages(nemen){
        fetch(`https://localhost:44317/api/Cseveges/kettofelhasznalo?kuldo=${user}&kapo=${nemen}`)
        .then(response => {
            return response.json()
        })
        .then(data=>{
            data.forEach(adat => {
                if(adat.Uzenet!=""){
                    return false
                }
            });
            return true
        })
    }

    fetch(`https://localhost:44317/api/Profil`)
        .then(response => {
            return response.json()
        })
        .then(data => {
            data.forEach(element => {
                if (user != element.Felhasznalonev) {

                    checkmessages(element.Felhasznalonev)



                    SendMessage(element.Felhasznalonev, "", true, true)
                }
            });
        })
}
